import React, { useCallback, useState } from "react";
import App from "./App";
import { graphql } from "babel-plugin-relay/macro";
import { useLazyLoadQuery, useMutation } from "react-relay/hooks";
import { ApprootQuery } from "./__generated__/ApprootQuery.graphql";
import { ApprootAddBookQuery } from "./__generated__/ApprootAddBookQuery.graphql";
import { ApprootAddBookQuery$variables } from "./__generated__/ApprootAddBookQuery.graphql";
import {
  ApprootupdateBookQuery,
  ApprootupdateBookQuery$variables,
} from "./__generated__/ApprootupdateBookQuery.graphql";
import { bookInput } from "./__generated__/ApprootAddBookQuery.graphql";
const appquery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  query ApprootQuery {
    ...App_detail
  }
`;

const appMutatequery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.

  mutation ApprootAddBookQuery($book: bookInput!) {
    addBook(book: $book) {
      title
      author {
        ...Author_detail
      }
      # title
    }
  }
`;

const appMutateupdatequery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.

  mutation ApprootupdateBookQuery($id: Int!, $book: bookInput!) {
    updateBook(bookId: $id, book: $book) {
      id
      title
      author {
        firstname
        secondname
      }
    }
  }
`;

function Approot(props: any) {
  // const [key, setKey] = useState(0);
  const [refreshedQueryOptions, setRefreshedQueryOptions] = useState<
    any | null
  >(null);
  const [commit] = useMutation<ApprootAddBookQuery>(appMutatequery);
  const [commitupdate] =
    useMutation<ApprootupdateBookQuery>(appMutateupdatequery);

  const refresh = useCallback(() => {
    setRefreshedQueryOptions((prev: any) => ({
      fetchkey: (prev?.fetchkey ?? 0) + 1,
      fetchPolicy: "network-only",
    }));
  }, []);

  const data = useLazyLoadQuery<ApprootQuery>(
    appquery,
    {},
    refreshedQueryOptions
    // { fetchPolicy: "network-only", fetchKey: key }
  );

  // console.log(data);

  return (
    <React.Suspense fallback={<div>Loading.....</div>}>
      <App books={data} />
      <form
        className="form"
        onSubmit={(e: any) => {
          e.preventDefault();
          console.log(typeof e.target[0].value);
          let sampeBook: bookInput = {
            // id: parseInt(e.target[0].value),

            id: e.target[0].value,
            title: e.target[1].value,
            author: {
              firstname: e.target[2].value,
              secondname: e.target[3].value,
            },
          };
          let idd: number = 1;

          let update: ApprootupdateBookQuery$variables = {
            id: idd,
            book: sampeBook,
          };
          // let optimisticResponse = {
          //   title: e.target[0].value,
          //   author: {
          //     firstname: e.target[1].value,
          //     secondname: e.target[2].value,
          //   },
          // };

          // commit({
          //   variables: update,
          //   onCompleted: (response, errors) => {
          //     console.log("Response received from server.");
          //     refresh();
          //     // setKey(key + 1);  that also works but we want that only when update fetch key then  fetch it network only else store or netowkr
          //   },
          //   onError: (err) => console.error(err),
          //   updater: (store) => {
          //     console.log(store.getRootField("addBook"));
          //   },
          // });

          // working

          // commitMutation(environment, {
          //   mutation: appMutatequery,
          //   variables: { book: sampeBook },
          //   onCompleted: (response, errors) => {
          //     console.log("Response received from server.");
          //     setKey(key + 1);
          //     // setTimeout(() => setKey(key + 1), 3000);
          //   },
          //   onError: (err) => console.error(err),
          //   // optimisticResponse,
          // });

          // update mutation

          commitupdate({
            variables: update,
            onCompleted: (response, errors) => {
              console.log("Response received from server.");
              // refresh();
              // setKey(key + 1);  that also works but we want that only when update fetch key then  fetch it network only else store or netowkr
            },
            onError: (err) => console.error(err),
            updater: (store) => {
              console.log(store.getRootField("addBook"));
            },
          });
        }}
      >
        <label htmlFor="fname">Book id:</label>
        <input type="number" id="fname" placeholder="Type Book id" /> <br></br>
        <label htmlFor="fname">Book title:</label>
        <input type="text" id="fname" placeholder="Type Book title" /> <br></br>
        <label htmlFor="fname">Author FirstName:</label>
        <input type="text" id="fname" placeholder="type author first name" />
        <br></br>
        <label htmlFor="fname">Author LastName:</label>
        <input type="text" id="fname" placeholder="Type author lastname" />
        <br></br>
        <input type="submit" />
      </form>
    </React.Suspense>
  );
}

export default Approot;
