import "./App.css";
import { graphql } from "babel-plugin-relay/macro";
import { useFragment } from "react-relay";
import { Author_detail$key } from "./__generated__/Author_detail.graphql";
const authorDetailFragment = graphql`
  # Fragments in graphql tags must start with the module name ('Author'). Got 'author_detial' instead.
  fragment Author_detail on Auth {
    firstname
    secondname
  }
`;

interface Props {
  authorname: Author_detail$key;
}

function Author({ authorname }: Props) {
  const data = useFragment(authorDetailFragment, authorname);
  //   console.log(data);

  return (
    <div className="author">
      <div>{data.firstname}</div>
      <div>{data.secondname}</div>
    </div>
  );
}

export default Author;
