import "./App.css";
import { graphql } from "babel-plugin-relay/macro";
import { useLazyLoadQuery, useMutation } from "react-relay";
import Author from "./Author";
import React, { useState } from "react";
import { AppQuery } from "./__generated__/AppQuery.graphql";
import {
  AppAddBookQuery,
  AppAddBookQuery$variables,
} from "./__generated__/AppAddBookQuery.graphql";
import { bookInput } from "./__generated__/AppAddBookQuery.graphql";
const appquery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  query AppQuery {
    books {
      title
      author {
        ...Author_detail
      }
    }
  }
`;

const appMutatequery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  mutation AppAddBookQuery($book: bookInput) {
    addBook(book: $book) {
      title
    }
  }
`;

// function HandleSubmit(e: any) {
//   e.preventDefault();
//   console.log(e.target[0].value);
//   console.log(e.target[1].value);
//   console.log(e.target[2].value);
//   console.log(e.target);
//   let newBook: bookInput = {
//     title: e.target[0].value,
//     author: {
//       firstname: e.target[1].value,
//       secondname: e.target[2].value,
//     },
//   };
//   const addedBook = useLazyLoadQuery<AppAddBookQuery>(appMutatequery, {
//     book: newBook,
//   });
//   console.log(addedBook);
// }

function App() {
  const [commit, isInFlight] = useMutation<AppAddBookQuery>(appMutatequery);
  const [on, onSet] = useState(0);
  // if (isInFlight) {
  //   return <div>Loading...</div>;
  // }
  const data = useLazyLoadQuery<AppQuery>(appquery, { on });

  let here: any = [];
  data.books.forEach((element) => {
    here.push(
      <div className="template" key={element.title}>
        <h1>{element.title}</h1>
        <Author authorname={element.author} />
      </div>
    );
  });

  return (
    <div>
      <ul>{here}</ul>

      <form
        onSubmit={(e: any) => {
          e.preventDefault();
          let sampeBook: bookInput = {
            title: e.target[0].value,
            author: {
              firstname: e.target[1].value,
              secondname: e.target[2].value,
            },
          };
          let update: AppAddBookQuery$variables = {
            book: sampeBook,
          };
          commit({
            variables: update,
            onCompleted(data) {
              console.log(data);
            },
          });
          onSet(1 - on);
        }}
        className="form"
      >
        <label htmlFor="fname">Book title:</label>
        <input type="text" id="fname" placeholder="Type Book title" /> <br></br>
        <label htmlFor="fname">Author FirstName:</label>
        <input type="text" id="fname" placeholder="type author first name" />
        <br></br>
        <label htmlFor="fname">Author LastName:</label>
        <input type="text" id="fname" placeholder="Type author lastname" />
        <br></br>
        <input type="submit" />
      </form>
    </div>
  );
}

function Approot() {
  return (
    <React.Suspense fallback={<div>Loading.....</div>}>
      <App />
    </React.Suspense>
  );
}

export default Approot;
