import React, { useCallback, useState } from "react";
import App from "./App";
import { graphql } from "babel-plugin-relay/macro";
import { useLazyLoadQuery, commitMutation } from "react-relay";
import Author from "./Author";
import { ApprootQuery } from "./__generated__/ApprootQuery.graphql";
import { bookInput } from "./__generated__/AppAddBookQuery.graphql";
import { ApprootAddBookQuery } from "./__generated__/ApprootAddBookQuery.graphql";
import { ApprootAddBookQuery$variables } from "./__generated__/ApprootAddBookQuery.graphql";
import environment from "./RelayEnvironment";

const appquery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  query ApprootQuery {
    ...App_detail
  }
`;

const appMutatequery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  mutation ApprootAddBookQuery($book: bookInput!) {
    addBook(book: $book) {
      title
      author {
        ...Author_detail
      }
      # title
    }
  }
`;

function Approot(props: any) {
  const [key, setKey] = useState(0);
  const data = useLazyLoadQuery<ApprootQuery>(
    appquery,
    {},
    { fetchPolicy: "store-and-network", fetchKey: key }
  );

  // console.log(data);

  return (
    <React.Suspense fallback={<div>Loading.....</div>}>
      <App books={data} />
      <form
        className="form"
        onSubmit={(e: any) => {
          e.preventDefault();
          let sampeBook: bookInput = {
            title: e.target[0].value,
            author: {
              firstname: e.target[1].value,
              secondname: e.target[2].value,
            },
          };
          // let update: ApprootAddBookQuery$variables = {
          //   book: sampeBook,
          // };
          // let optimisticResponse = {
          //   title: e.target[0].value,
          //   author: {
          //     firstname: e.target[1].value,
          //     secondname: e.target[2].value,
          //   },
          // };
          commitMutation(environment, {
            mutation: appMutatequery,
            variables: { book: sampeBook },
            onCompleted: (response, errors) => {
              console.log("Response received from server.");
              // setKey(key + 1);
              // setTimeout(() => setKey(key + 1), 3000);
            },
            onError: (err) => console.error(err),
            // optimisticResponse,
          });
        }}
      >
        <label htmlFor="fname">Book title:</label>
        <input type="text" id="fname" placeholder="Type Book title" /> <br></br>
        <label htmlFor="fname">Author FirstName:</label>
        <input type="text" id="fname" placeholder="type author first name" />
        <br></br>
        <label htmlFor="fname">Author LastName:</label>
        <input type="text" id="fname" placeholder="Type author lastname" />
        <br></br>
        <input type="submit" />
      </form>
    </React.Suspense>
  );
}

export default Approot;
