import "./App.css";
import { graphql } from "babel-plugin-relay/macro";
import { useFragment, useLazyLoadQuery, useMutation } from "react-relay";
import Author from "./Author";
import React, { useState } from "react";
import { App_detail$key } from "./__generated__/App_detail.graphql";
import { Title_detail$key } from "./__generated__/Title_detail.graphql";

const titleDetailFragment = graphql`
  # Fragments in graphql tags must start with the module name ('Author'). Got 'author_detial' instead.
  fragment Title_detail on Book {
    title
    author {
      ...Author_detail
    }
  }
`;

type Props = {
  aBook: Title_detail$key;
};

function Title({ aBook }: Props) {
  return (
    <div className="template">
      <h1>{aBook.bookData.title}</h1>
      <Author authorname={aBook.bookData.author} />
    </div>
  );
}

export default Title;
