import "./App.css";
import { graphql } from "babel-plugin-relay/macro";
import { useFragment, useLazyLoadQuery, useMutation } from "react-relay";
import Author from "./Author";
import React, { useState } from "react";
import { App_detail$key, App_detail } from "./__generated__/App_detail.graphql";
import {
  AppAddBookQuery,
  AppAddBookQuery$variables,
} from "./__generated__/AppAddBookQuery.graphql";

const bookDetailFragment = graphql`
  # Fragments in graphql tags must start with the module name ('Author'). Got 'author_detial' instead.
  fragment App_detail on Query {
    books {
      title
      author {
        ...Author_detail
      }
    }
  }
`;

type Props = {
  books: App_detail$key;
};

function App({ books }: Props) {
  const data = useFragment<App_detail>(books, {});
  let here: any = [];
  data.books.forEach((element) => {
    here.push(
      <div className="template" key={element.title}>
        <h1>{element.title}</h1>
        <Author authorname={element.author} />
      </div>
    );
  });

  return (
    <div>
      <ul>{here}</ul>
    </div>
  );
}

export default App;
