import "./App.css";
import { graphql } from "babel-plugin-relay/macro";
import { useLazyLoadQuery } from "react-relay";
import Author_detail from "./Author";
import React from "react";
import { AppQuery } from "./__generated__/AppQuery.graphql";

const appquery = graphql`
  # Queries in graphql tags must start with the module name ('App') and end with 'Query'. Got 'allbook' instead.
  query AppQuery {
    books {
      title
      # author {
      #   ...Author_detail
      # }
    }
  }
`;

function App() {
  const data = useLazyLoadQuery<AppQuery>(appquery, {});
  console.log(data);

  console.log(data.books[0]);

  return (
    <div>
      hello
      <React.Suspense fallback={<div>Loading.....</div>}>
        {/* <div>{data.books[0].title}</div> */}
        {/* <Author data={data.books[0].author} /> */}
      </React.Suspense>
    </div>
  );
}

export default App;
